    border-top:    1px solid white;
    border-bottom: 1px solid white;
}
.myExample>.hs-layout {
    border:    1px solid white;
    background-color: #ccc;
}
.myExample {
    color:       #a44;
    font-size: 10px;
    font-weight: bold;
    text-align:  center;
}
</file>
</example>

